export class Request {
    get(url, settings = {}) {
        settings.url = url;
        settings.method = "GET";
        return window.ANU.Rx.DOM.ajax(settings);
    }
    post(url, settings = {}) {
        settings.url = url;
        settings.method = "POST";
        return window.ANU.Rx.DOM.ajax(settings);
    }
    put(url, settings = {}) {
        settings.url = url;
        settings.method = "PUT";
        return window.ANU.Rx.DOM.ajax(settings);
    }
    delete(url, settings = {}) {
        settings.url = url;
        settings.method = "DELETE";
        return window.ANU.Rx.DOM.ajax(settings);
    }
}